const getMultiPolygon = (): [number, number][][] => {
    let a: [number, number][][] = [];
    const lakes = 1000;
    const h = 0.05;
    const w = 0.1;
    const cols = 50;
    const segments = 200;
    const r = 0.003;
    const origin = [51.51, 90.0];
    for (let i = 0; i < lakes; i++) {
        let dy = h * Math.floor(i / cols);
        let dx = w * (i % cols);
        let p: [number, number][] = [];
        for (let j = 0; j < segments; j++) {
            const rad = 2 * Math.PI / segments * j;
            const rMod = r * (1 + 0.2 * Math.sin(rad * 10));
            p[j] = [
                origin[0] + Math.cos(rad) * rMod + dy,
                origin[1] + Math.sin(rad) * rMod + dx
            ]
        }
        a[i] = p;
/*        a[i] = [
            [51.51 + dy, -0.12 + dx],
            [51.51 + dy, -0.13 + dx],
            [51.53 + dy, -0.13 + dx],
        ];*/
    }
    console.log(a);
    return a;
};

